# @ <SNIPPET>
from __future__ import print_function
import sys
from openeye import oechem
from openeye import oegrid

ifs = oechem.oemolistream(sys.argv[1])
mol = oechem.OEGraphMol()
oechem.OEReadMolecule(ifs, mol)

# @ <SNIPPET-OEMakeGridFromCenterAndExtents>
center = oechem.OEFloatArray(3)
extents = oechem.OEFloatArray(3)
spacing = 0.5
oechem.OEGetCenterAndExtents(mol, center, extents)

grid = oegrid.OEScalarGrid()
oegrid.OEMakeGridFromCenterAndExtents(grid, center, extents, spacing)
# @ </SNIPPET-OEMakeGridFromCenterAndExtents>

print("Mid: %.6f %.6f %.6f" % (grid.GetXMid(), grid.GetYMid(), grid.GetZMid()))
print("Dim: %d %d %d" % (grid.GetXDim(), grid.GetYDim(), grid.GetZDim()))
print("Spacing: %.6f" % grid.GetSpacing())
# @ </SNIPPET>
