# @ <SNIPPET>
from openeye import oechem

# @ <SNIPPET-READMOL>
mol = oechem.OEGraphMol()
success = oechem.OEReadMolFromBytes(mol, ".smi", "c1ccccc1")
# @ </SNIPPET-READMOL>

# @ <SNIPPET-READMOL2>
mol = oechem.OEGraphMol()
success = oechem.OEReadMolFromBytes(mol, oechem.OEFormat_SMI, False, "c1ccccc1")
# @ </SNIPPET-READMOL2>

# @ <SNIPPET-READMOL3>
mol = oechem.OEGraphMol()
success = oechem.OEReadMolFromBytes(mol, oechem.OEFormat_SMI,
                                    oechem.OEGetDefaultIFlavor(oechem.OEFormat_SMI),
                                    False, "c1ccccc1")
# @ </SNIPPET-READMOL3>

# @ <SNIPPET-WRITEMOL>
mol = oechem.OEGraphMol()
oechem.OESmilesToMol(mol, "c1ccccc1")
molbytes = oechem.OEWriteMolToBytes(".smi", mol)
# @ </SNIPPET-WRITEMOL>

# @ <SNIPPET-WRITEMOL2>
mol = oechem.OEGraphMol()
oechem.OESmilesToMol(mol, "c1ccccc1")
molbytes = oechem.OEWriteMolToBytes(oechem.OEFormat_SMI, False, mol)
# @ </SNIPPET-WRITEMOL2>

# @ <SNIPPET-WRITEMOL3>
mol = oechem.OEGraphMol()
oechem.OESmilesToMol(mol, "c1ccccc1")
molbytes = oechem.OEWriteMolToBytes(oechem.OEFormat_SMI,
                                    oechem.OEGetDefaultOFlavor(oechem.OEFormat_SMI),
                                    False, mol)
# @ </SNIPPET-WRITEMOL3>
# @ </SNIPPET>
