# @ <SNIPPET>
from __future__ import print_function
from openeye import oechem

mol = oechem.OEGraphMol()
oechem.OESmilesToMol(mol, "C1[NH]C=CC=1CO")

# @ <SNIPPET-RING-LOOP1>
for atom in mol.GetAtoms(oechem.OENotAtom(oechem.OEAtomIsInRing())):
    print(atom.GetIdx(), oechem.OEGetAtomicSymbol(atom.GetAtomicNum()))
# @ </SNIPPET-RING-LOOP1>

# @ <SNIPPET-RING-LOOP2>
for atom in mol.GetAtoms():
    if not atom.IsInRing():
        print(atom.GetIdx(), oechem.OEGetAtomicSymbol(atom.GetAtomicNum()))
# @ </SNIPPET-RING-LOOP2>

mol.Clear()
oechem.OESmilesToMol(mol, "C1CC2CC1CC2")
# @ <SNIPPET-RING-SMALLEST-SIZE>
for atom in mol.GetAtoms():
    size = oechem.OEAtomGetSmallestRingSize(atom)
    if size == 0:
        print(atom.GetIdx(), "acyclic")
    else:
        print(atom.GetIdx(), "smallest ring size=", size)
# @ </SNIPPET-RING-SMALLEST-SIZE>

for bond in mol.GetBonds():
    size = oechem.OEBondGetSmallestRingSize(bond)
    if size == 0:
        print(bond.GetIdx(), "acyclic")
    else:
        print(bond.GetIdx(), "smallest ring size=", size)

mol.Clear()
oechem.OESmilesToMol(mol, "c1cccc2c1[nH]cc2")
print("fused atoms:")
# @ <SNIPPET-RING-SIZE>
for atom in mol.GetAtoms():
    if oechem.OEAtomIsInRingSize(atom, 5) and oechem.OEAtomIsInRingSize(atom, 6):
        print(atom.GetIdx())
# @ </SNIPPET-RING-SIZE>
# @ </SNIPPET>
