"""
 Test suite for Grapheme TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('grapheme') is False,
                    reason='Grapheme license not found')
class TestGrapheme(OETestCase):

    def test_bfactor2img(self):
        script = self.run_example('{0}/grapheme/bfactor2img.py'.format(self.EXAMPLES_DIR),
                                  '-complex', '{0}/1FCX.pdb.gz'.format(self.DATA_DIR),
                                  '-out', 'bfactor2img.png', expect_stderr=True)
        self.assertFalse(script.returncode)
