"""
 Test suite for OEChem TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('oechem') is False,
                    reason='OEChem license not found')
class TestOEChem(OETestCase):

    def test_oecheminfo(self):
        script = self.run_example('{0}/oechem/oecheminfo.py'.format(self.EXAMPLES_DIR))
        self.assertFalse(script.returncode)

    def test_molgrep(self):
        script = self.run_example(
            '{0}/oechem/molgrep.py'.format(self.EXAMPLES_DIR),
            '-i', '{0}/data.smi'.format(self.DATA_DIR),
            '-p', '[C]' '-o', 'molgrep.smi')
        self.assertFalse(script.returncode)

    def test_aromaticty(self):
        script = self.run_example('{0}/oechem/Aromaticity.py'.format(self.DOCEXAMPLES_DIR))
        self.assertFalse(script.returncode)

    def test_backbone(self):
        script = self.run_example(
            '{0}/bio/backbone.py'.format(self.EXAMPLES_DIR),
            '{0}/1d3h.pdb'.format(self.DATA_DIR),
            'backbone.pdb')
        self.assertFalse(script.returncode)

    def test_make_alpha(self):
        script = self.run_example(
            '{0}/bio/makealpha.py'.format(self.EXAMPLES_DIR),
            '{0}/data.smi'.format(self.DATA_DIR),
            'makealpha.smi')
        self.assertFalse(script.returncode)

    def test_ascii_writer(self):
        script = self.run_example('{0}/grid/ASCIIWriter.py'.format(self.DOCEXAMPLES_DIR))
        self.assertFalse(script.returncode)

    def test_spatial_coords_access(self):
        script = self.run_example(
            '{0}/grid/SpatialCoordAccess.py'.format(self.DOCEXAMPLES_DIR),
            '{0}/data3d.sdf.gz'.format(self.DATA_DIR))
        self.assertFalse(script.returncode)
