"""
 Test suite for OEDepict TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('depict') is False, reason='OEDepict license not found')
class TestOEDepict(OETestCase):

    def test_depict(self):
        script = self.run_example(
            '{0}/depict/depict.py'.format(self.EXAMPLES_DIR),
            '-in', '{0}/data.smi'.format(self.DATA_DIR),
            '-out', 'depict.sdf')
        self.assertFalse(script.returncode)

    def test_mol2img(self):
        script = self.run_example(
            '{0}/depict/mol2img.py'.format(self.EXAMPLES_DIR),
            '-in', '{0}/data.smi'.format(self.DATA_DIR),
            '-out', 'mol2img.svg')
        self.assertFalse(script.returncode)

    def test_mdlreaction2img(self):
        script = self.run_example(
            '{0}/depict/mdlreaction2img.py'.format(self.EXAMPLES_DIR),
            '{0}/reaction.rxn'.format(self.DATA_DIR),
            'depictmdl.svg')
        self.assertFalse(script.returncode)
