"""
 Test suite for Shape TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('shape') is False, reason='SHAPE license not found')
class TestShape(OETestCase):

    def test_shape_props(self):
        script = self.run_example(
            '{0}/shape/shapeprops.py'.format(self.EXAMPLES_DIR),
            '{0}/data3d.sdf.gz'.format(self.DATA_DIR), expect_stderr=True)
        self.assertFalse(script.returncode)
