"""
 Test suite for Spruce TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('spruce') is False, reason='SPRUCETK license not found')
class TestSpruce(OETestCase):

    def test_spruce_simplesuperposition(self):
        script = self.run_example(
            '{0}/spruce/simplesuperposition.py'.format(self.EXAMPLES_DIR),
            '{0}/1A29.pdb'.format(self.DATA_DIR),
            '{0}/1CLL.pdb'.format(self.DATA_DIR), expect_stderr=True)
        self.assertFalse(script.returncode)

    def test_spruce_secondarystructuresuperposition(self):
        script = self.run_example(
            '{0}/spruce/secondarystructuresuperposition.py'.format(self.EXAMPLES_DIR),
            '{0}/1A29.pdb'.format(self.DATA_DIR),
            '{0}/1CLL.pdb'.format(self.DATA_DIR), expect_stderr=True)
        self.assertFalse(script.returncode)
