"""
 Test suite for Szmap TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('szmap') is False, reason='SzmapTK license not found')
class TestSzmap(OETestCase):

    def test_SzmapEnergies(self):
        script = self.run_example('{0}/szmap/SzmapEnergies.py'.format(self.EXAMPLES_DIR),
                                  '-p', '{0}/4std_prot.oeb.gz'.format(self.DATA_DIR),
                                  '-l', '{0}/4std_lig.oeb.gz'.format(self.DATA_DIR),
                                  expect_stderr=True)
        self.assertFalse(script.returncode)

    def test_SzmapBestOrientations(self):
        script = self.run_example('{0}/szmap/SzmapBestOrientations.py'.format(self.EXAMPLES_DIR),
                                  '-p', '{0}/4std_prot.oeb.gz'.format(self.DATA_DIR),
                                  '-l', '{0}/4std_lig.oeb.gz'.format(self.DATA_DIR),
                                  '-o', 'szmap_orientations.oeb.gz', expect_stderr=True)
        self.assertFalse(script.returncode)
