"""
 Test suite for Szybki TK examples
"""

import pytest
from . import OETestCase, check_license


@pytest.mark.skipif(check_license('szybki') is False, reason='SZYBKI license not found')
class TestSzybki(OETestCase):

    def test_example1(self):
        script = self.run_example(
            '{0}/szybki/simple_vacuum.py'.format(self.EXAMPLES_DIR),
            '{0}/data3d.sdf.gz'.format(self.DATA_DIR),
            'szybkiexample1.smi')
        self.assertFalse(script.returncode)
